import React, { Component } from "react";
import "./App.css";
import Control from "./components/Control";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";
import { v4 as uuidv4 } from 'uuid';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tasks: []
        }
    }

    createData  = () => {
        const tasks = [
            {
                id: uuidv4(),
                name: "Học lập trình Java",
                status: true
            }
        ];
    }
  render() {
    return (
      <div>
        <div className="container">
          <div className="text-center">
            <h1>Quản Lý Công Việc</h1>
            <hr />
          </div>
          <div className="row">
            <div className="col-xs-12 col-sm-12 col-md-4 col-lg-4">
              <TaskForm />
            </div>
            <div className="col-xs-12 col-sm-12 col-md-8 col-lg-8">
              <button type="button" className="btn btn-primary mb-10">
                <i className="fa fa-plus mr-3"></i>Thêm Công Việc
              </button>

              <button type="button" className="btn btn-primary mb-10" onClick={this.createData}>
                <i className="fa fa-plus mr-3"></i>Tao
              </button>
              <Control />
              <div className="row mt-15">
                <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                    <TaskList/>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
