import React, { Component } from "react";
import "./App.css";
import Control from "./components/Control";
import TaskForm from "./components/TaskForm";
import TaskList from "./components/TaskList";
import { v4 as uuidv4 } from 'uuid';
import * as constants from "./constants/index"
class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            tasks: [],
            isDisplayForm: false
        }
    }

    componentDidMount(){
        const tasks = JSON.parse(localStorage.getItem(constants.DATA_TASK_LIST))

        //Check if an array is empty or exists
        if(Array.isArray(tasks) && tasks.length){
            this.setState({tasks: tasks})
        }
    }

    get
  render() {
    return (
      <div>
        <div className="container">
          <div className="text-center">
            <h1>Quản Lý Công Việc</h1>
            <hr />
          </div>
          <div className="row">
            <div className="col-xs-12 col-sm-12 col-md-4 col-lg-4">
              {this.state.isDisplayForm && <TaskForm />}
              {/* <TaskForm /> */}
            </div>
            <div className={this.state.isDisplayForm ? "col-xs-12 col-sm-12 col-md-8 col-lg-8": "col-xs-12 col-sm-12 col-md-8 col-lg-8 col-12"}>
              <button type="button" className="btn btn-primary mb-10">
                <i className="fa fa-plus mr-3"></i>Thêm Công Việc
              </button>
              <Control />
              <div className="row mt-15">
                <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
                    <TaskList
                        tasks={this.state.tasks}
                    />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
